local font_large = love.graphics.newFont(16)
-- extra func for renderinfo

local function lowconn_area(x, y, w, h)
	love.graphics.setColor(1, .2, .2, .2)
	love.graphics.rectangle("fill", TILE_SIZE * x, TILE_SIZE * y, TILE_SIZE * w, TILE_SIZE * h)
	love.graphics.setColor(1, .2, .2, .4)
	love.graphics.setLineWidth(4)
	love.graphics.rectangle("line", TILE_SIZE * x, TILE_SIZE * y, TILE_SIZE * w, TILE_SIZE * h)
end

local function hint(text, x, y)
	love.graphics.print(text, font_large, TILE_SIZE * x, TILE_SIZE * y)
end


ConnJam.DeclareMapExtraRender("level1", function()
	-- we are translated to map
	love.graphics.setColor(1, 1, 1)

	hint("A/D to move", 7.5, 12)

	hint("SPACE to jump", 26, 12)

	hint("Avoid low connection areas", 32, 8)
	hint("(marked RED by your sensor)", 32, 8.25)

	hint("Deliver products to the CUSTOMER", 45.5, 12)

	lowconn_area(35, 9.25, 6, 3)
end)