-- extra func for renderinfo


local function lowconn_area(x, y, w, h)
	love.graphics.setColor(1, .2, .2, .2)
	love.graphics.rectangle("fill", TILE_SIZE * x, TILE_SIZE * y, TILE_SIZE * w, TILE_SIZE * h)
	love.graphics.setColor(1, .2, .2, .4)
	love.graphics.setLineWidth(4)
	love.graphics.rectangle("line", TILE_SIZE * x, TILE_SIZE * y, TILE_SIZE * w, TILE_SIZE * h)
end


